const mongoose = require('mongoose');

const restaurantSchema = new mongoose.Schema({
    name: { type: String, required: true },
    location: { type: String, required: true },
    cuisine: { type: String, required: true },
    rating: { type: Number, min: 1, max: 5 }
});

const Restaurant = mongoose.model('Restaurant', restaurantSchema);
module.exports = Restaurant;

const express = require('express');
const Restaurant = require('./models/restaurant');

const app = express();

app.get('/api/v1/restaurants', async (req, res) => {
    try {
        const restaurants = await Restaurant.find();
        res.json(restaurants);
    } catch (err) {
        res.status(500).json({ message: 'Error fetching restaurants' });
    }
});

app.post('/api/v1/addRestaurant', async (req, res) => {
    const { name, location, cuisine, rating } = req.body;

    if (!name || !location || !cuisine) {
        return res.status(400).json({ message: 'Name, location, and cuisine are required' });
    }

    try {
        const newRestaurant = new Restaurant({ name, location, cuisine, rating });
        await newRestaurant.save();
        res.status(201).json({ message: 'Restaurant added successfully' });
    } catch (err) {
        res.status(500).json({ message: 'Error adding restaurant' });
    }
});

app.delete('/api/v1/deleteRestaurant/:id', async (req, res) => {
    const { id } = req.params;

    try {
        const deletedRestaurant = await Restaurant.findByIdAndDelete(id);
        if (!deletedRestaurant) {
            return res.status(404).json({ message: 'Restaurant not found' });
        }
        res.status(200).json({ message: 'Restaurant deleted successfully' });
    } catch (err) {
        res.status(500).json({ message: 'Error deleting restaurant' });
    }
});

